const marxDataId = MARX_DATA.map((entry, index) => Object.assign({}, { id: index, value: entry }))

app.get('/fetch', (req, res) => {
  const { string } = req.query;
  const resArr = string ? marxDataId
    .filter(entry => entry.value.toLowerCase().includes(string.toLowerCase()))
    : marxDataId.slice(0, 20);
  res.send(resArr);
});

app.get('/fetchAutocomplete/:type', async (req, res) => {
  const { string, resource } = req.query;
  const { type } = req.params;
  let resArr = [];
  if (type === 'title' || type === 'doi') {
    resArr = string ? scienceData
      .filter(entry => entry && entry[type] && entry[type].toLowerCase()
        .includes(string.toLowerCase()))
      : [];
  }
  if (type === 'authors' || type === 'journal') {
    resArr = string ? scienceData
      .reduce((prev, curr) => prev.concat(curr[type]), [])
      .filter(prop => prop.toLowerCase().includes(string.toLowerCase())) : [];
  }
  // remove double entries
  resArr = Array.from(new Set(resArr));
  // .reduce((prev, curr) => (prev.includes(curr) ? prev : prev.concat(curr)), []);
  let gndRes = [];
  let extResults = [];
  if (type === 'authors') {
    if (string) {
      extResults = await getExternalData(string, resource);
    }
    resArr = resArr.map(item => Object.assign({}, { [type]: item, source: 'Local Authors' }));
    gndRes = string ? gndData.filter(item => item.name.toLowerCase().includes(string.toLowerCase()))
      .map(result => Object.assign({}, { [type]: result.name, born: result.born, source: 'Local Papers' })) : [];
    resArr = gndRes.concat(extResults, resArr);
  }
  res.send(resArr);
});

app.get('/data/:id', async (req, res) => {
  let result = [];
  const { resource } = req.query;
  const { id } = req.params;
  result = await getExternalData(id, resource, true);
  res.send(result);
});

